children.value:
details {
  border: 1px solid #aaa;
  border-top-right-radius:45px;
  border-bottom-left-radius:45px;
  margin-top:15px;
  /* border-radius: 4px; */
  padding: .5em .5em 0;
  margin-bottom: 1em;
}

summary {

  font-weight: bold;
  margin: -.5em -.5em 0;
  padding: .5em;
  cursor: pointer;
  list-style:none;
}

details[open] {
  padding: .5em;
}

details[open] summary {
  border-bottom: 1px solid #aaa;
  margin-bottom: .5em;
}

.title{
  color:yellow;
  font-size:26px;
  text-align:right;
  margin-bottom:25px;
  margin-top:25px;
}
.font-custom{
  color:#f97316;
  text-decoration-line: underline;
  text-decoration-style: double;
  text-decoration-thickness: 2px;
  text-decoration-color: #f97316;
}
.font-custom2{
  color:lime;
  text-decoration-line: underline;
  text-decoration-style: wavy;
  text-decoration-thickness: 2px;
  text-decoration-color: lime;
}
